CREATE TABLE `auth`.`users` (`id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(55) NOT NULL , `bio` TEXT NULL DEFAULT NULL , `photo` VARCHAR(255) NULL DEFAULT NULL , `cover` VARCHAR(255) NULL DEFAULT NULL , `email` VARCHAR(255) NOT NULL , `phone` VARCHAR(20) NULL DEFAULT NULL , `birthday` DATE NULL DEFAULT NULL , `gender` ENUM('male','female') NULL DEFAULT NULL , `location` VARCHAR(55) NULL DEFAULT NULL , `job_title` VARCHAR(55) NULL DEFAULT NULL , `password` VARCHAR(255) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL , PRIMARY KEY (`id`), UNIQUE (`email`), UNIQUE (`phone`)) ENGINE = InnoDB;
CREATE TABLE `auth`.`posts` (`id` INT(11) NOT NULL AUTO_INCREMENT , `user_id` INT(11) NOT NULL , `text` TEXT NOT NULL , `created_at` TIMESTAMP NOT NULL , `updated_at` TIMESTAMP NOT NULL , PRIMARY KEY (`id`), INDEX (`user_id`)) ENGINE = InnoDB;
ALTER TABLE `posts` ADD CONSTRAINT `user` FOREIGN KEY (`user_id`) REFERENCES `users`(`id`) ON DELETE CASCADE ON UPDATE CASCADE;
